mapper.select:
SELECT COUNT(*) cnt
  FROM INQUIRY
 WHERE PARENT_ID = #{parentid}

SELECT apilist.API_LIST_ID , API_NM , PRV_ORG , API_EXPL , API_RGDT , apilist.API_APPLY_ID , apiapply.APPLY_NM API_APPLY_NM , COUNT(*) OVER() TOTAL FROM API_LIST apilist LEFT JOIN API_APPLY apiapply ON apilist.API_APPLY_ID = apiapply.API_APPLY_ID WHERE 1 = 1
AND apilist.API_LIST_ID = #{apilistid}
ORDER BY API_LIST_ID DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT API_APPLY_ID , APIAPPLY.USER_ID USER_ID , APPLY_NM , APPLY_CNTN , APPLY_DATE , RPLY_DATE , APPLY_DVCD , user.USER_NAME USER_NAME , apilist.API_LIST_ID API_LIST_ID , apilist.API_NM apinm , COUNT(*) OVER() TOTAL FROM API_APPLY APIAPPLY INNER JOIN USER user ON APIAPPLY.USER_ID = user.USER_ID LEFT JOIN API_LIST apilist ON apilist.API_LIST_ID = APIAPPLY.API_LIST_ID WHERE 1 = 1
AND API_APPLY_ID = #{apiapplyid}
ORDER BY API_APPLY_ID DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT inquiry.INQUIRY_ID , INQUIRY_NM , INQUIRY_CNTN , REG_DT , PARENT_ID , inquiry.USER_ID USER_ID , user.USER_NAME USER_NAME , reply.REPLY_COUNT , COUNT(*) OVER() TOTAL FROM INQUIRY inquiry INNER JOIN USER user ON user.USER_ID = inquiry.USER_ID LEFT JOIN (SELECT inquiry1.INQUIRY_ID INQUIRY_ID , COUNT(*) REPLY_COUNT FROM INQUIRY inquiry1 , INQUIRY inquiry2 WHERE inquiry1.INQUIRY_ID = inquiry2.PARENT_ID GROUP BY inquiry1.INQUIRY_ID) reply ON inquiry.INQUIRY_ID = reply.INQUIRY_ID WHERE PARENT_ID IS NULL
AND inquiry.INQUIRY_ID = #{inquiryid}
ORDER BY INQUIRY_ID DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT API_KEY_ID , SITE_DVCD , API_KEY , STRDT , EDT FROM API_KEY
WHERE API_KEY_ID = #{apikeyid}
ORDER BY SITE_DVCD LIMIT #{pageSize} OFFSET #{offset}

SELECT ID , CODE , CODE_LABEL , CODE_VALUE FROM COM_CODE
WHERE ID = #{id}
ORDER BY CODE,CODE_LABEL

mapper.insert:
INSERT INTO API_KEY(SITE_DVCD, API_KEY, STRDT, EDT, FST_RGRP, FST_RG_DTTI, LST_CHPR, LST_CH_DTTI)
VALUES (#{apikeydto.sitedvcd}, #{apikeydto.apikey}, #{apikeydto.strdt}, #{apikeydto.edt}
       , #{userid}
       , CONCAT(DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), LPAD(FLOOR(EXTRACT(MICROSECOND FROM SYSDATE(3)) / 1000), 3, '0'))
       , #{userid}
       , CONCAT(DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), LPAD(FLOOR(EXTRACT(MICROSECOND FROM SYSDATE(3)) / 1000), 3, '0')))

INSERT INTO COM_CODE(CODE, CODE_LABEL, CODE_VALUE, FST_RGRP, FST_RG_DTTI, LST_CHPR, LST_CH_DTTI)
VALUES (#{comcodeDto.code}, #{comcodeDto.codelabel}, #{comcodeDto.codevalue}
       , #{userid}
       , CONCAT(DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), LPAD(FLOOR(EXTRACT(MICROSECOND FROM SYSDATE(3)) / 1000), 3, '0'))
       , #{userid}
       , CONCAT(DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), LPAD(FLOOR(EXTRACT(MICROSECOND FROM SYSDATE(3)) / 1000), 3, '0')))

mapper.update:
UPDATE API_LIST
   SET API_NM = #{apiresultDto.apinm}
     , PRV_ORG = #{apiresultDto.prvorg}
     , API_EXPL = #{apiresultDto.apiexpl}
     , API_RGDT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
     , API_APPLY_ID = CASE WHEN #{apiresultDto.apiapplyid} = 0 THEN NULL ELSE #{apiresultDto.apiapplyid} END
 WHERE API_LIST_ID = #{apiresultDto.apilistid}

UPDATE API_APPLY
   SET RPLY_DATE = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
     , APPLY_DVCD = #{apiapplyDto.applydvcd}
 WHERE API_APPLY_ID = #{apiapplyDto.apiapplyid}

UPDATE INQUIRY
   SET INQUIRY_NM = #{inquiryDto.inquirynm}
     , INQUIRY_CNTN = #{inquiryDto.inquirycntn}
     , REG_DT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
     , USER_ID = #{userid}
 WHERE PARENT_ID = #{inquiryDto.parentid}

UPDATE API_KEY
   SET SITE_DVCD = #{apikeydto.sitedvcd}
     , API_KEY = #{apikeydto.apikey}
     , STRDT = #{apikeydto.strdt}
     , EDT = #{apikeydto.edt}
     , LST_CHPR = #{userid}
     , LST_CH_DTTI = CONCAT(DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), LPAD(FLOOR(EXTRACT(MICROSECOND FROM SYSDATE(3)) / 1000), 3, '0'))
 WHERE API_KEY_ID = #{apikeydto.apikeyid}

  UPDATE COM_CODE
  SET CODE = #{comcodeDto.code}
    , CODE_LABEL = #{comcodeDto.codelabel}
    , CODE_VALUE = #{comcodeDto.codevalue}
    , LST_CHPR = #{userid}
    , LST_CH_DTTI = CONCAT(DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), LPAD(FLOOR(EXTRACT(MICROSECOND FROM SYSDATE(3)) / 1000), 3, '0'))
WHERE ID = #{comcodeDto.id}
