mapper.select:
SELECT BATCH_LIST_ID , batchlist.USER_ID USER_ID , batchlist.API_LIST_ID , FREQUENCY , TIME , DAY_OF_WEEK , DAY_OF_MONTH , STCD , apilist.API_NM apinm FROM BATCH_LIST batchlist INNER JOIN API_LIST apilist ON apilist.API_LIST_ID = batchlist.API_LIST_ID WHERE batchlist.USER_ID = #{userid} AND batchlist.STCD = "01"
AND BATCH_LIST_ID = #{batchlistid}
ORDER BY BATCH_LIST_ID DESC

SELECT ALARM_ID , TITLE , CONTENT , REG_DT , READ_CHECK , send_user.USER_ID SEND_USER , rcv_user.USER_ID RCV_USER , send_user.USER_NAME SEND_USERNAME , rcv_user.USER_NAME RCV_USERNAME FROM ALARM alarm INNER JOIN USER send_user ON send_user.USER_ID = alarm.SEND_USER INNER JOIN USER rcv_user ON rcv_user.USER_ID = alarm.RCV_USER AND alarm.RCV_USER = #{userid} WHERE 1 = 1
AND ALARM_ID = #{alarmid}
ORDER BY ALARM_ID DESC

mapper.update:
UPDATE BATCH_LIST
   SET STCD = "99"
 WHERE USER_ID = #{userid}
   AND BATCH_LIST_ID = #{batchlistid}

UPDATE BATCH_LIST
   SET BATCH_TIME = #{batchlistDto.batchtime}
     , FREQUENCY = #{batchlistDto.frequency}
     , TIME = #{batchlistDto.time}
     , DAY_OF_WEEK = #{batchlistDto.dayofweek}
     , DAY_OF_MONTH = #{batchlistDto.dayofmonth}
 WHERE USER_ID = #{userid}
   AND BATCH_LIST_ID = #{batchlistDto.batchlistid}

UPDATE ALARM
   SET READ_CHECK = TRUE
 WHERE ALARM_ID = #{alarmid}
   AND RCV_USER = #{userid}
