mapper.select:
SELECT COUNT(*)
  FROM API_USER_INTRS
 WHERE USER_ID = #{arg0} and API_LIST_ID = #{arg1}

mapper.insert:
INSERT INTO API_USES (USER_ID, API_LIST_ID, API_USE_DATE, FST_RGRP, FST_RG_DTTI, LST_CHPR, LST_CH_DTTI)
VALUES (#{userid}, #{apilistid}, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
       , #{userid}
       , CONCAT(DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), LPAD(FLOOR(EXTRACT(MICROSECOND FROM SYSDATE(3)) / 1000), 3, '0'))
       , #{userid}
       , CONCAT(DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), LPAD(FLOOR(EXTRACT(MICROSECOND FROM SYSDATE(3)) / 1000), 3, '0')))

mapper.update:
UPDATE API_USER_INTRS
   SET STCD = #{arg1.stcd}
 WHERE USER_ID = #{arg0} and API_LIST_ID = #{arg1.apilistid}
